#include<stdio.h>
#include<string.h>
#include<iostream>
using namespace std;

char f[1009][3000],r[4][3000];

void smul(char a[],int b)
{
    int i,j,k,l,s,m;
    k=strlen(a);
    l=strlen(f[b]);

    for(j=0;j<k;j++)
    {
        s=0;m=0;
        for(;m<j;m++)
        {
            r[j][m]=48;
        }
        for(i=0;i<l;i++)
        {
            s+=(f[b][i]-48)*(a[j]-48);
            r[j][m]=s%10+48;
            s/=10;
            m++;
        }
        while(s!=0)
        {
            r[j][m]=s%10+48;
            s/=10;
            m++;
        }
        r[j][m]='\0';
    }

    s=0;i=0;
    while(i<strlen(r[j-1]))
    {
        for(m=0;m<k;m++)
        {
            if(i<strlen(r[m]))
                s+=r[m][i]-48;
        }
        f[b+1][i]=s%10+48;
        s/=10;
        i++;
    }
    while(s!=0)
    {
        f[b+1][i]=s%10+48;
        s/=10;
        i++;
    }
    f[b+1][i]='\0';
}


int main()
{
    int n,i,x,j;
    char a[10];
    strcpy(f[1],"2");
    strcpy(f[0],"1");
    strcpy(f[699],"");
    while(scanf("%d",&n)!=EOF)
    {
        if(n==0){cout<<n<<"!"<<endl<<"1"<<endl;continue;}
        if(strlen(f[n-1])==0){
                i=3;
        if(n>700)i=701;
        for(;i<=n;i++)
        {
            if(strlen(f[i-1])>0)continue;
            x=i;j=0;
            while(x!=0)
            {
                a[j]=x%10+48;
                x/=10;
                j++;
            }
            a[j]='\0';
            smul(a,i-2);
        }
        }
        cout<<n<<"!"<<endl;
        for(i=strlen(f[n-1])-1;i>-1;i--)
        cout<<f[n-1][i];
        cout<<endl;
    }
    return 0;
}
